mapper.select:
SELECT user_id, user_email, user_pw, user_name, user_birth, created_at
FROM tb_user
WHERE user_email = #{email}

SELECT user_id, user_email, user_pw, user_name, user_birth, created_at
FROM tb_user
WHERE user_id = #{userId}

mapper.update:
UPDATE tb_user
SET user_name = #{userName},
    user_birth = #{userBirth}
WHERE user_id = #{userId}
